import React, { useState } from 'react';
import Draggable from 'react-draggable';
import './FilterBuilder.css';

const initialModules = [
  { id: '1', content: 'rb', type: 'attribute' },
  { id: '2', content: 'drb', type: 'attribute' },
  { id: '3', content: 'galactic_latitude', type: 'attribute' },
  { id: '4', content: 'jd', type: 'attribute' },
  { id: '5', content: 'jdstarthist', type: 'attribute' },
  { id: '6', content: 'AND', type: 'operator' },
  { id: '7', content: 'OR', type: 'operator' },
  { id: '8', content: '<', type: 'comparator' },
  { id: '9', content: '<=', type: 'comparator' },
  { id: '10', content: '==', type: 'comparator' },
  { id: '11', content: '>', type: 'comparator' },
  { id: '12', content: '>=', type: 'comparator' },
  { id: '13', content: '!=', type: 'comparator' },
  { id: '14', content: 'Number', type: 'input' },
  { id: '15', content: 'age', type: 'attribute' },
];

const FilterBuilder = () => {
  const [workspace, setWorkspace] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);
  const [filterExpression, setFilterExpression] = useState('');

  const handleModuleClick = (module) => {
    const newModule = { ...module, id: `${module.id}-${Date.now()}` };
    setWorkspace([...workspace, newModule]);
    if (module.type === 'input') {
      setEditingIndex(workspace.length);
    }
  };

  const handleRemoveModule = (index) => {
    const newWorkspace = Array.from(workspace);
    newWorkspace.splice(index, 1);
    setWorkspace(newWorkspace);
  };

  const handleClearWorkspace = () => {
    setWorkspace([]);
  };

  const handleAddAttribute = () => {
    const attributeName = prompt('Enter the name of the new attribute:');
    if (attributeName) {
      const newAttribute = {
        id: `attr-${Date.now()}`,
        type: 'attribute',
        content: attributeName,
      };
      setWorkspace([...workspace, newAttribute]);
    }
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleAddInputModule = (index) => {
    const newWorkspace = Array.from(workspace);
    newWorkspace[index].content = inputValue;
    setWorkspace(newWorkspace);
    setInputValue('');
    setEditingIndex(null);
  };

  const handleModuleDoubleClick = (index) => {
    if (workspace[index].content === 'Number') {
      setEditingIndex(index);
    } else {
      handleRemoveModule(index);
    }
  };

  const handleKeyPress = (e, index) => {
    if (e.key === 'Enter') {
      handleAddInputModule(index);
    }
  };

  const handleCreateFilter = () => {
    const placeholderGroup = {
      id: `placeholder-group-${Date.now()}`,
      type: 'placeholder-group',
      content: [
        { id: `placeholder-1-${Date.now()}`, content: 'Placeholder 1', type: 'placeholder' },
        { id: `placeholder-2-${Date.now()}`, content: 'Placeholder 2', type: 'placeholder' },
        { id: `placeholder-3-${Date.now()}`, content: 'Placeholder 3', type: 'placeholder' },
      ],
    };
    setWorkspace([...workspace, placeholderGroup]);
  };

  return (
    <div className="filter-builder">
      <div className="module-palette">
        <div className="attributes-operators">
          <h3>Attributes</h3>
          {initialModules.filter(module => module.type === 'attribute').map((module) => (
            <div
              key={module.id}
              className="module"
              onClick={() => handleModuleClick(module)}
            >
              {module.content}
            </div>
          ))}
          <h3>Operators</h3>
          {initialModules.filter(module => module.type === 'operator').map((module) => (
            <div
              key={module.id}
              className="module"
              onClick={() => handleModuleClick(module)}
            >
              {module.content}
            </div>
          ))}
        </div>
        <div className="comparators-input">
          <h3>Comparators</h3>
          {initialModules.filter(module => module.type === 'comparator').map((module) => (
            <div
              key={module.id}
              className="module"
              onClick={() => handleModuleClick(module)}
            >
              {module.content}
            </div>
          ))}
          <h3>Inputs</h3>
          {initialModules.filter(module => module.type === 'input').map((module) => (
            <div
              key={module.id}
              className="module"
              onClick={() => handleModuleClick(module)}
            >
              {module.content}
            </div>
          ))}
        </div>
      </div>
      <div className="graph-workspace-container">
        <div className="graph-workspace">
        {workspace.map((module, index) => (
          module.type === 'placeholder-group' ? (
            <Draggable key={module.id}>
              <div className="placeholder-group">
                {module.content.map((placeholder, idx) => (
                  <div key={placeholder.id} className={`module placeholder ${idx === 1 ? 'middle' : ''}`}>
                    {placeholder.content}
                  </div>
                ))}
                <div className="line left-line"></div>
                <div className="line right-line"></div>
              </div>
            </Draggable>
          ) : (
            <Draggable key={module.id}>
              <div className="module" onDoubleClick={() => handleModuleDoubleClick(index)}>
                {editingIndex === index ? (
                  <input
                    type="text"
                    value={inputValue}
                    onChange={handleInputChange}
                    onKeyPress={(e) => handleKeyPress(e, index)}
                    placeholder="Enter value"
                  />
                ) : (
                  module.content
                )}
              </div>
            </Draggable>
          )
        ))}
        </div>
        <div className="button-container">
          <button className="create-filter-button" onClick={handleCreateFilter}>Create Filter</button>
          <button className="add-attribute-button" onClick={handleAddAttribute}>Add Attribute</button>
          <button className="clear-button" onClick={handleClearWorkspace}>Clear Workspace</button>
        </div>
        {filterExpression && (
          <div className="filter-expression">
            <h3>Generated Filter:</h3>
            <p>{filterExpression}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default FilterBuilder;
